CREATE TABLE lines (
    id integer primary key,
    name_sv text,
    name_fi text,
    name_en text
);

CREATE TABLE stops (
    id integer primary key,
    name text,
    lat decimal,
    lng decimal
);

CREATE TABLE line_segments (
    id integer primary key,
    from_stop integer references stops(id),
    to_stop integer references stops(id),
    line integer references lines(id),
    geometry text
);






